import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, ImageBackground } from 'react-native';

export default function App({navigation}) {

  const handleNextPress = () => {

    navigation.navigate("homePage")

  }

  return (

    <View style={{flex: 1}}>

      <ImageBackground source={require("../../assets/background.png")} style={styles.image}>

        <Text style={styles.text1}>Bem vindo ao Elite,{"\n"}
                                   o lugar onde você{"\n"}
                                   irá encontar empresas{"\n"}
                                   para a sua necessidade</Text>

        <TouchableOpacity style={styles.roundButton1} onPress={handleNextPress}>

          <Text style={styles.text2}>Entrar</Text>

        </TouchableOpacity>

        <Text style={styles.text3} >Cadastre sua empresa</Text>

      </ImageBackground>

    </View>

  );
}

const styles = StyleSheet.create({

  image: {
    flex:1,
    resizeMode: "cover",
    alignItems: "center",
    justifyContent: "flex-end"
  },

  roundButton1: {
    width: 250,
    height: 50,
    marginTop: 20,
    marginBottom: 15,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
    borderRadius: 25,
    backgroundColor: "#9400d4",
  },

  text1: {

    fontWeight: "bold",
    fontSize: 30,
    color: "#fff"

  },

  text2: {

    fontWeight: "bold",
    fontSize: 15,
    color: "#fff"

  },

  text3: {

    fontSize: 15,
    color: "#fff",
    marginBottom: 30

  }

});
